          historyCheck: "",
          //头像
          photo: "",
          leftView: "",
          rightView: "",
          correctLeftView: "",
          correctRightView: "",
          //其他眼疾
          otherEyesSick: "",
          //彩色图案及编码
          colorOrCode: "",
          //单色识别
          singleColorJudge: "",
          leftAudition: 0,
          rightAudition: 0,
          earSick: "",
          smell: "",
          //鼻窦
          paranasalSinus: "",
          //咽喉
          throat: "",
          //口吃
          stammer: "",
          toothSick: "",
