          hypodontia: "",
          //齿槽
          toothPlace: "",
          //其他无关疾病
          otherSenseSick: "",
          //五官医师签字
          signForSense: "",
          suggestForEyes: "",
          suggestForEar: "",
          suggestForTooth: "",
          height: 0,
          weight: 0,
          //腰围
          waistline: 0,
          skin: "",
          //淋巴
          lymph: "",
          //甲状腺
          glandulaThyroidea: "",
          //脊柱
          spineBackbone: "",
          //四肢
          allLegs: "",
          //关节
          arthrosis: "",
          foot: "",
          otherSurgerySick: "",
          signForSurgery: "",
          suggestForSurgery: "",
          //血压
          bloodPressure: 0,
          //脉搏
          pulse: 0,
          //发育营养
          growth: "",
          //神经及精神
          mind: "",
          //肺及呼吸道
          lung: "",
          //心脏及血管
          heart: "",
          //肝
          liver: "",
          //脾
          spleen: "",
          //其他内科疾病
          otherInternalSick: "",
          signForInternal: "",
          suggestForInternal: "",
          //化验检查
          assayCheck: "",
          signForAssay: "",
          //胸部放射检查
          chestCheck: "",
          signForChest: "",
          otherCheck: "",
          checkResult: "",
          signForResult: "",
          suggestForCheck: "",
          //盖章
          sealer: "",
